<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');include_once(APPPATH . 'models/Base_model.php');class Changelog_model extends Base_model {	public function __construct() {		parent::__construct();		$this->table_name = 'change_log';	}    public function groupBy($where,$limit){        return $this->db->group_by("user_id,type")->select_sum('count')->select('user_id')->order_by('count','desc')            ->limit($limit)->where($where)->get($this->table_name)->result_array();    }}