<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');include_once(APPPATH . 'models/Base_model.php');class Course_chapter_model extends Base_model {	public function __construct() {		parent::__construct();		$this->table_name = 'course_chapter';	}	public function create($params) {		$new_id = parent::create($params);	}	public function get_lists($params){		return $this->db->where($params)->order_by('id', 'asc')->get($this->table_name)->result_array();	}}