<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');include_once(APPPATH . 'models/Base_model.php');class Problem_comment_model extends Base_model {	public function __construct() {		parent::__construct();		$this->table_name = 'problem_comment';	}	public function add_comment($owner_id , $problem_id , $content){		$this->db->insert($this->table_name, array(	            'owner_id' => $owner_id,	            'problem_id' => $problem_id,	            'content' => $content	        ));		return $this->db->insert_id();	}	public function delete_all($id){		$this->db->delete($this->table_name,array("owner_id" => $id));		return true;	}	public function get_list($params, $page = 0, $count = 20) {		$this->load->model('problem_model');		$comments = parent::get_list($params, $page, $count);		foreach($comments as &$comment) {			$comment['author'] = $this->user_model->get(array(				'id' => $comment['owner_id']			))['nickname'];			$comment['problem_title'] = $this->problem_model->get(array(				'id' => $comment['problem_id']			))['title'];		}		return $comments;	}}