<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');include_once(APPPATH . 'models/Base_model.php');class Problem_comment_model extends Base_model {	public function __construct() {		parent::__construct();		$this->table_name = 'problem_comment';	}	public function add_comment($owner_id , $problem_id , $content){		$this->db->insert($this->table_name, array(	            'owner_id' => $owner_id,	            'problem_id' => $problem_id,	            'content' => $content	        ));		return $this->db->insert_id();	}	public function delete_all($id){		$this->db->delete($this->table_name,array("owner_id" => $id));		return true;	}}