<?php if (!defined('BASEPATH')) exit('No direct script access allowed');include_once(APPPATH . 'models/Base_model.php');class Problem_model extends Base_model {	public function __construct() {		parent::__construct();		$this->load->model('tag_model');		$this->load->model('news_model');		$this->table_name = 'problem';		$this->me=$this->user_model->check_login();	}	public function get_collect($json , $page = 0, $count = 10, $type = "collect"){		$collect = json_decode($json);		if($collect == array())return array();		foreach ($collect as $key => $value) {			$this->db->or_where("id" , $value->t);		}		$this->db->limit($page * $count , $count);		$data = $this->db->get($this->table_name)->result_array();		$index = 0;		foreach($data as $item) {			$data[$index]['ctime'] = date("H:i:s",strtotime($item['ctime']));			@$data[$index]['tags'] = $this->tag_model->get_list_by_json($data[$index]['tags']);			$index ++;		}		return $data;	}	public function get_answer($uid , $page = 0 , $count = 10 , $where = "answer_id"){		$this->db->limit($count , ($page -1)* $count);		$data = $this->db->get_where($this->table_name,array(			$where => $uid,		))->result_array();		$index = 0;		foreach($data as $item) {			$data[$index]['ctime'] = date("H:i:s",strtotime($item['ctime']));			@$data[$index]['tags'] = $this->tag_model->get_list_by_json($data[$index]['tags']);			$index ++;		}		 return $data;	}	public function answer_count($uid,$where = "answer_id"){		return $this->db->where(array(			$where => $uid		))->count_all_results($this->table_name);	}	// pid, uid	public function request($params) {		extract($params);		$data = $this->db->get_where($this->table_name,array('id' => $params['pid']))->result_array();		if($data[0]["type"] == "1" || $data[0]["type"] == "2"){			return false;		}		$this->db->where(array(			'id' => $params['pid'],			'type' => "0"		))->update($this->table_name, array(			'answer_id' => $params['uid'],			'answer_time' =>  time(),			'type' => 1		));		$problem_data = $this->db->get_where($this->table_name , array("id" =>$params['pid']))->result_array();		$this->news_model->add_news($problem_data[0]["owner_id"],"大神".$this->me["nickname"]."认领了您的问题，" . $problem_data[0]['title']);		return $this->db->affected_rows() > 0;	}	public function def($pid) {		$this->db->where(array(			'id' => $pid,			'type' => 1		))->update($this->table_name, array(			'type' => 0		));		return $this->db->affected_rows() > 0;	}	public function no($pid) {		$this->db->where(array(			'id' => $pid,			'type' => 2		))->update($this->table_name, array(			'type' => 0		));		return $this->db->affected_rows() > 0;	}	public function done($pid) {		$this->db->where(array(			'id' => $pid,			'type' => 1		))->update($this->table_name, array(			'type' => 2		));		$problem_data = $this->db->get_where($this->table_name , array("id" =>$pid))->result_array();		$this->news_model->add_news($problem_data[0]["owner_id"],"大神".$this->me["nickname"]."认领了您的问题，" . $problem_data[0]['title']);		return $this->db->affected_rows() > 0;	}	public function close($pid) {		$this->db->where(array(			'id' => $pid['pid'],			'type' => 2		))->update($this->table_name, array(			'type' => 3		));		return $this->db->affected_rows() > 0;	}	public function get_list_by_id($id) {		$list = $this->db->where('id', $id)->get($this->table_name)->row_array();		$list['tags'] = $this->tag_model->get_list_by_json($list['tags']);		$list = count($list) <= 0 ? false : $list;		return $list;	}	public function get_list_by_tag($tag_name = "" , $type = "ctime" , $page = 0, $count = 20) {		$index = 0;		$this->db->order_by($type,'DESC');		$list = $this->db->like('tags', $tag_name)->limit($count, $page * $count)->get($this->table_name)->result_array();		foreach($list as $item) {			$list[$index]['ctime'] = date("H:i:s",strtotime($item['ctime']));			@$list[$index]['tags'] = $this->tag_model->get_list_by_json($list[$index]['tags']);			$index ++;		}		$list = count($list) <= 0 ? false : $list;		return $list;	}	public function get_list_by_tag_count($tag_name = ""){		return $this->db->like('tags', $tag_name)->count_all_results($this->table_name);	}	public function get_list_count(){		return $this->db->count_all_results($this->table_name);	}	public function get_list_by_time($page = 0, $count = 20) {		$list = parent::get_list(array(), $page, $count);		$index = 0;		foreach($list as $item) {			$list[$index]['ctime'] = date("H:i:s",strtotime($item['ctime']));			@$list[$index]['tags'] = $this->tag_model->get_list_by_json($item['tags']);			$index ++;		}		return $list;	}	public function get_list_by_hot($page = 0, $count = 20,$type="desc") {		$list = $this->db->order_by('hot',$type)->limit($count, $page * $count)->get($this->table_name)->result_array();		$index = 0;		foreach($list as $item) {			$list[$index]['ctime'] = date("H:i:s",strtotime($item['ctime']));			@$list[$index]['tags'] = $this->tag_model->get_list_by_json($item['tags']);			$index ++;		}		return $list;	}	public function follow($pid) {		$this->db->query("update `" . $this->table_name ."` SET			`hot` = `hot` + 3 ,			`follow_count` = `follow_count` + 1			WHERE `id` = $pid");		return $this->db->affected_rows() > 0;	}	public function unfollow($pid) {		$this->db->query("update `" . $this->table_name ."` SET			`hot` = `hot` - 3 ,			`follow_count` = `follow_count` - 1			WHERE `id` = $pid");		return $this->db->affected_rows() > 0;	}	// 收藏问题	public function collect($pid) {		if(!$this->user_model->collect_problem($pid))return false;		$this->db->query("update `" . $this->table_name ."` SET			`hot` = `hot` + 3 ,			`collect_count` = `collect_count` + 1			WHERE `id` = $pid");		return $this->db->affected_rows() > 0;	}	public function uncollect($pid) {		if(!$this->user_model->uncollect_problem($pid))return false;		$this->db->query("update `" . $this->table_name ."` SET			`hot` = `hot` - 3 ,			`collect_count` = `collect_count` - 1			WHERE `id` = $pid");		return $this->db->affected_rows() > 0;	}	public function get_problem($pid){		$data = $this->db->select(array("up_count" , "hot" ,"up_users"))->get_where($this->table_name , array("id" => $pid) , 0,1)->result_array();		return $data;	}	public function up($array) {		$this->db->where('id', $array['id'])->update($this->table_name, array(			'up_count' => $array["up_count"],			'hot' =>  $array["hot"],			'up_users' =>   $array["up_users"]		));		return $this->db->affected_rows() > 0;	}	public function down($pid) {		$this->db->where('id', $pid)->update($this->table_name, array(			'up_count' => $data[0]['up_count'] - 1,			'hot' => $data[0]['hot'] - 5,			'up_users ' =>  json_encode($up_users)		));		return $this->db->affected_rows() > 0;	}}