<?php if (!defined('BASEPATH')) exit('No direct script access allowed');include_once(APPPATH . 'models/base_model.php');class problem_detail_model extends base_model {	public function __construct() {		parent::__construct();		$this->load->model('tag_model');		$this->load->model('user_model');		$this->table_name = 'problem_detail';	}	public function get_detaill($id){		$index = 0;		$detaill = $this->db->get_where($this->table_name,array("problem_id" => $id))->result_array();		foreach ($detaill as $key => $value) {			@$detaill[$index]['user'] = $this->user_model->get_user_data($detaill[$index]['owner_id']);			$index ++;		}		return $detaill;	}}