<?php if (!defined('BASEPATH')) exit('No direct script access allowed');include_once(APPPATH . 'models/base_model.php');class problem_model extends base_model {	public function __construct() {		parent::__construct();		$this->load->model('tag_model');		$this->table_name = 'problem';	}	// pid, uid	public function request($params) {		extract($params);		$this->db->where(array(			'id' => $pid,			'type' => 0		))->update($this->table_name, array(			'answer_id' => $uid,			'type' => 1		));		return $this->db->affected_rows() > 0;	}	public function done($pid) {		$this->db->where(array(			'id' => $pid,			'type' => 1		))->update($this->table_name, array(			'type' => 2		));		return $this->db->affected_rows() > 0;	}	public function close($pid) {		$this->db->where(array(			'id' => $pid,			'type' => 2		))->update($this->table_name, array(			'type' => 3		));		return $this->db->affected_rows() > 0;	}	public function get_list_by_time($page = 0, $count = 20) {		$list = parent::get_list(array(), $page, $count);		foreach($list as $item) {			$item['tags'] = $this->tag_model->get_list_by_json($item['json']);		}		return $list;	}	public function get_list_by_hot($page = 0, $count = 20) {		$list = $this->db->order_by('hot', 'DESC')->limit($count, $page * $count)->get($this->table_name)->result_array();		foreach($list as $item) {			$item['tags'] = $this->tag_model->get_list_by_json($item['json']);		}		return $list;	}	public function follow($pid) {		$this->db->where('id', $pid)->update($this->table_name, array(			'follow_count' => 'follow_count + 1',			'hot' => 'hot + 3'		));	}	public function unfollow($pid) {		$this->db->where('id', $pid)->update($this->table_name, array(			'follow_count' => 'follow_count - 1',			'hot' => 'hot - 3'		));	}	public function collect($pid) {		$this->db->where('id', $pid)->update($this->table_name, array(			'collect_count' => 'collect_count + 1',			'hot' => 'hot + 3'		));	}	public function uncollect() {		$this->db->where('id', $pid)->update($this->table_name, array(			'collect_count' => 'collect_count - 1',			'hot' => 'hot - 3'		));	}	public function up() {		$this->db->where('id', $pid)->update($this->table_name, array(			'up_count' => 'up_count + 1',			'hot' => 'hot + 5'		));	}	public function down() {		$this->db->where('id', $pid)->update($this->table_name, array(			'up_count' => 'down_count + 1',			'hot' => 'hot - 5'		));	}}