<?php

class tag_model extends CI_Model {	public function __construct(){		parent::__construct();	}    public function create($params) {		$this->db->insert('tag', $params);    }	public function remove($id) {		$this->db->delete('tag', array(			'id' => $id		));	}	public function online($id) {		$this->db->where('id', $id)->update('tag', array(			'status' => 1		));	}	public function offline($id) {		$this->db->where('id', $id)->update('tag', array(			'status' => 0		));	}	public function edit($id, $params) {		$this->db->where('id', $id)->update('tag', $params);	}	public function isExist($params) {		return $this->db->select('id')->where($params)->get('tag')->num_rows() > 0;	}	public function getById($id) {		return $this->db->where(array(			'status' => 1,			'id' => $id		))->get('tag')->row_array();	}    public function getByName($name) {		return $this->db->where(array(			'status' => 1,			'name' => $name		))->get('tag')->row_array();    }    public function plus($id, $count = 1) {        $query = $this->db->select('count')->where('id', $id)->get('slide')->row_array();        $this->db->where('id', $id)->update('slide', array(            'count' => $query['count'] + $count        ));    }    public function minus($id, $count = 1) {        $query = $this->db->select('count')->where('id', $id)->get('slide')->row_array();        $this->db->where('id', $id)->update('slide', array(            'count' => $query['count'] - $count        ));    }}