<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');include_once(APPPATH . 'models/base_model.php');class user_model extends base_model {	public function __construct() {		parent::__construct();		$this->table_name = 'user';		$this->id_name = 'uid';		$this->me = $this->check_login();	}	public function login($username, $pwd) {		$user = $this->db->select('id, pwd, salt')->where('name', $username)->get($this->table_name)->row_array();		if(count($user) <= 0){			return false;		}		if (empty($user) || $user['pwd'] !== md5($pwd . $user['salt'])) return '用户名或密码错误';		$this->session->set_userdata($this->id_name, $user['id']);		return true;	}	public function logout() {		$this->session->unset_userdata($this->id_name);		return true;	}	public function check_login() {		$id = $this->session->userdata($this->id_name);		if (!isset($id)) return false;		$userdata = $this->get(array(			"id" => $id		));		if($userdata["avatar"] == NULL){			$userdata["avatar"] = "static/image/default.jpg";		}		return $userdata;	}	public function get_user_data($id){		$userdata = parent::get(array("id"=>$id ));		if(count($userdata) <=0 )return false;		if($userdata["avatar"] == NULL){			$userdata["avatar"] = "static/image/default.jpg";		}		return $userdata;	}	// name, nickname, pwd	public function create($params) {		extract($params);		if ($this->is_exist(array('name' => $name))) return '用户名已存在';		$salt = substr(uniqid(rand()), -10);		return parent::create(array(			'nickname' => $nickname,			'name' => $name,			'salt' => $salt,			'pwd' => md5($pwd . $salt)		));	}	public function follow_problem($uid, $pid) {		$this->db->where('id', $uid)->update($this->table_name, array());	}	public function unfollow_problem() {	}	public function collect_problem() {	}	public function uncollect_problem() {	}}